sys_script_client.description:
//Created by - Udaya Sadhineni
//Created on - 06 Feb 2019
//Purpose - To populate Project name on change of Project number on Invoice Project Task

sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
	if (isLoading) {
		return;
	}

	if(window == null){
		g_form.clearValue('assignment');
	//	g_form.setValue('project_lvl3_name','');
		var projectNo = newValue;
		var ga = new GlideAjax('ScriptIncludeClientCallableUtils');
		ga.addParam('sysparm_name','setName');
		ga.addParam('number',projectNo);
		ga.getXML(setUserValues);
	}
	function setUserValues(response) {
		var answer = response.responseXML.documentElement.getAttribute("answer");
		g_form.setValue('project_lvl2_name',answer);
	}

}
